def findMarkerStart(input):
    for i in range(len(input)):
        count = 1
        seen = [input[i]]
        for y in range(i+1, len(input)):
            if input[y] in seen:
                break
            else:
                count += 1
                seen.append(input[y])
                if count == 4:
                    print(seen)
                    return y+1


